import React, { useContext, useEffect, useState } from "react";
import WalletContext from "../../context/Wallet";
import DropFactoryContext from "../../context/StakingRewardsFactory";
import useToggle from "../../hooks/useToggle.js";
import useForm from "../../hooks/useForm.js";
import CreateDrop from '../../components/StakingRewardsFactory';

const ConnectWalletButton = () => {
  const { connectWalletHandler } = useContext(WalletContext);

  return (
    <button
      onClick={connectWalletHandler}
      className="cta-button connect-wallet-button"
    >
      Connect Wallet
    </button>
  );
};


const Main = () => {
  const { currentAccount } = useContext(WalletContext);

  return (
    <div className="main-app">
      <h1>DEX</h1>
      {currentAccount ? <CreateDrop /> : <ConnectWalletButton />}
      <div className="drops-grid">

      </div>
    </div>
  );
};

export default Main;
