import React, { useContext } from "react";
import DropFactoryContext from "../../context/DropFactory";
import useToggle from "../../hooks/useToggle.js";
import useForm from "../../hooks/useForm.js";

const CreateDrop = () => {
  const { createDrop } = useContext(DropFactoryContext);
  const showForm = useToggle();
  const showStak = useToggle();

  const maxSupply = useForm({
    type: "text",
    placeholder: "to",
  });
  const name = useForm({ type: "number", placeholder: "name of the drop" });
  const cid = useForm({
    type: "text",
    placeholder: "cid from ipfs or use a body of your url",
  });
  const prefix = useForm({ type: "text", placeholder: "ipfs:// or https" });
  const sufix = useForm({
    type: "text",
    placeholder: "extension of your file e.j .json",
  });
  const hiddenURI = useForm({
    type: "text",
    placeholder: "URI from the nft loading image",
  });
  const maxPerTX = useForm({
    type: "number",
    placeholder: "max mint per transaction",
  });

  /*
  const crateDropHandler = async (e) => {
    e.preventDefault();
    await createDrop(
      maxSupply.value,
      name.value,
      cid.value,
      prefix.value,
      sufix.value,
      hiddenURI.value,
      maxPerTX.value
    );
  };
  */

  return (
    <>
      <button onClick={showForm.toggle} className="cta-button mint-nft-button">
        {!showForm.show ? "Deposit" : "Withdraw"}
      </button>
      {showForm.show && (
        <form>
          <div className="drop-form">
            <input {...maxSupply} />
            <input {...name} />
          </div>
          <div className="drop-form">
            <input {...cid} />
            <input {...prefix} />
          </div>
          <div className="drop-form">
            <input {...sufix} />
            <input {...hiddenURI} />
          </div>
          <input {...maxPerTX} />
          <button onClick={showStak.toggle} className="cta-button showStak"></button>

          <button className="cta-button showStak">ddddd</button>

          <button className="cta-button mint-nft-button">remove</button>
        </form>
      )}
      {!showForm.show && (

        <form>
          <h2>Add Liquidity</h2>
          <div className="drop-form">
            <input {...maxSupply} />
            <input {...name} />
          </div>
          <div className="drop-form">
            <input {...cid} />
            <input {...prefix} />
          </div>
          <div className="drop-form">
            <input {...sufix} />
            <input {...hiddenURI} />
          </div>
          <input {...maxPerTX} />

          <button className="cta-button mint-nft-button">add</button>
        </form>
      )}
    </>
  );
};
export default CreateDrop;


