import React, { createContext, useEffect, useState } from "react";
//import DropFactoryContract from "../../Contracts/NftDrop.json";
import StakingRewardsFactoryContract from "../../Contracts/StakingRewards.json";
import { ethers } from "ethers";
//DropFactory
//StakingRewardsFactory

const StakingRewardsFactoryContext = createContext();

export function StakingRewardsFactoryProvider({ children }) {
  const address = StakingRewardsFactoryContract.address;
  const abi = StakingRewardsFactoryContract.abi;
  const [StakingRewardsFactory, setStakingRewardsFactory] = useState(null);
  useEffect(() => {
    const { ethereum } = window;
    if (!ethereum) return alert("Install metamask");
    const provider = new ethers.providers.Web3Provider(ethereum);
    const signer = provider.getSigner();
    const StakingRewardsFactoryContract = new ethers.Contract(address, abi, signer);
    setStakingRewardsFactory(StakingRewardsFactoryContract);
  }, [abi, address]);



  const addLiq = async (
      _tokenB,
      _amountTokenB,
      _amountTokenMin,
      _amountEthMin
      ) => {
    try {
      const tx = await StakingRewardsFactory.addLiquidityEth(
        "0x6B175474E89094C44Da98b954EedeAC495271d0F",
        _amountTokenB,
        _amountTokenMin,
        _amountEthMin,
        {
          gasLimit: 750000,
          value: ethers.utils.parseEther(_amountEth),
        }
      );
      console.log("loading...");
      await tx.wait();
      alert("added liquidity " + tx.hash);
    } catch (error) {
      alert(error.message);
    }
  };


  const stakeWithPermit = async (
    _amount,
		_deadline,
		_v,
		_r,
		_s
    ) => {
  try {
    const tx = await StakingRewardsFactory.stakeWithPermit(
      _amount,
      _deadline,
      _v,
      _r,
      _s
    );
    console.log("loading...");
    await tx.wait();
    alert("added liquidity " + tx.hash);
  } catch (error) {
    alert(error.message);
  }
};


  const stake = async (
    _amount
    ) => {
  try {
    const tx = await StakingRewardsFactory.stake(
      ethers.BigNumber.from(ethers.BigNumber.from(_amount)),      
    );
    console.log("loading...");
    await tx.wait();
    alert("stake token LP " + tx.hash);
  } catch (error) {
    alert(error.message);
  }
};


const unstake = async (
    _amount
    ) => {
try {
    const tx = await StakingRewardsFactory.unstake(
      _amount      
    );
    console.log("loading...");
    await tx.wait();
    alert("unstake token LP " + tx.hash);
  } catch (error) {
    alert(error.message);
  }
};


const claimTokens = async (
    _amount
    ) => {
try {
    const tx = await StakingRewardsFactory.claimTokens(
      _amount      
    );
    console.log("loading...");
    await tx.wait();
    alert("unstake token LP " + tx.hash);
  } catch (error) {
    alert(error.message);
  }
};



  return (
    <StakingRewardsFactoryContext.Provider
      value={{
        address,
        abi,
        StakingRewardsFactory,
        addLiq,
        stake,
        unstake,
        claimTokens,
        stakeWithPermit
      }}
    >
      {children}
    </StakingRewardsFactoryContext.Provider>
  );
}
export default StakingRewardsFactoryContext;








