import React, { useContext } from "react";
import DropFactoryContext from "../../context/DropFactory";
import useToggle from "../../hooks/useToggle.js";
import useForm from "../../hooks/useForm.js";

const CreateDrop = () => {
  const { addLiq,stake,unstake } = useContext(DropFactoryContext);
  const showForm = useToggle();
  const showStak = useToggle();


  const tokenA = useForm({
    type: "number",
    placeholder: "token A",
  });

  const tokenB = useForm({
    type: "number",
    placeholder: "amount token A",
  });

  const tokenC = useForm({
    type: "number",
    placeholder: "token B",
  });

  const tokenD = useForm({
    type: "number",
    placeholder: "amount token A",
  });

  const tokenStakAdd = useForm({
    type: "number",
    placeholder: "LP token to stake",
  });

  const maxSupply = useForm({
    type: "number",
    placeholder: "max suply of nfts",
  });

  const name = useForm({ type: "number", placeholder: "name of the drop" });
  const cid = useForm({
    type: "text",
    placeholder: "cid from ipfs or use a body of your url",
  });
  const prefix = useForm({ type: "text", placeholder: "ipfs:// or https" });
  const sufix = useForm({
    type: "text",
    placeholder: "extension of your file e.j .json",
  });
  const hiddenURI = useForm({
    type: "text",
    placeholder: "URI from the nft loading image",
  });
  const maxPerTX = useForm({
    type: "number",
    placeholder: "max mint per transaction",
  });


  const preventDefault = async (e) => {
    e.preventDefault();
    console.log(e)
  };

  const addLiquidy = async (e) => {
    e.preventDefault();
    await addLiq(
      tokenA.value,
      tokenB.value,
      tokenC.value,
      tokenD.value,
      tokenD.value,

    );
    if(showForm.show){
      await stake(tokenStakAdd);
    }
  };



  return (
    <>
      <button onClick={showForm.toggle} className="cta-button mint-nft-button">
        {!showForm.show ? "change to Deposit" : "change to Withdraw"}
      </button>
      {showForm.show && (
        <form onSubmit={preventDefault}>
          <div className="drop-form">
            <input {...tokenA} />
            <input {...tokenB} />
          </div>
          <div className="drop-form">
            <input {...tokenC} />
            <input {...tokenD} />
          </div>

          <button onClick={showStak.toggle} className="cta-button mint-nft-button">
            {!showStak.show ? "token staking show" : "token staking hide"}
          </button>
          {showStak.show && (
          <input {...tokenStakAdd} />
          )}

          <button onClick={addLiquidy} className="cta-button mint-nft-button">confirm</button>
        </form> 
      )}
      {!showForm.show && (

        <form>
          <h2>Add Liquidity</h2>
          <div className="drop-form">
            <input {...maxSupply} />
            <input {...maxSupply} />
          </div>
          <div className="drop-form">
            <input {...maxSupply} />
            <input {...maxSupply} />
          </div>
          <div className="drop-form">
            <input {...sufix} />
            <input {...hiddenURI} />
          </div>
          <input {...maxPerTX} />

          <button className="cta-button mint-nft-button">confirm</button>


        </form>
      )}
    </>
  );
};
export default CreateDrop;


