import React, { useContext } from "react";
import StakingRewardsContext from "../../context/StakingRewardsFactory";
import useToggle from "../../hooks/useToggle.js";
import useForm from "../../hooks/useForm.js";

const CreateDrop = () => {
  const { addLiq,stake,unstake,claimTokens } = useContext(StakingRewardsContext);
  const showForm = useToggle();
  const showStak = useToggle();


  const tokenA = useForm({
    type: "number",
    placeholder: "token A",
  });

  const tokenB = useForm({
    type: "number",
    placeholder: "amount token A",
  });

  const tokenC = useForm({
    type: "number",
    placeholder: "token B",
  });

  const tokenD = useForm({
    type: "number",
    placeholder: "amount token A",
  });

  const tokenStakAdd = useForm({
    type: "number",
    placeholder: "LP token to stake",
  });

  const amountWithdrawal = useForm({
    type: "number",
    placeholder: "amount withdrawal",
  });




  const preventDefault = async (e) => {
    e.preventDefault();
    console.log(e)
  };

  const addLiquidy = async (e) => {
    e.preventDefault();
    await addLiq(
      tokenA.value,
      tokenB.value,
      tokenC.value,
      tokenD.value,
      tokenD.value,

    );
    /*if(showForm.show){
      await stake(tokenStakAdd.value);
    }*/
  };

  const removeLiquidy = async (e) => {
    e.preventDefault();
    await claimTokens(
      amountWithdrawal.value
    );
  };

  return (
    <>
      <button onClick={showForm.toggle} className="cta-button mint-nft-button">
        {!showForm.show ? "change to Deposit" : "change to Withdraw"}
      </button>
      {showForm.show && (
        <form onSubmit={preventDefault}>
          <h2>add liquidity</h2>
          <div className="drop-form">
            <input {...tokenA} />
            <input {...tokenB} />
          </div>
          <div className="drop-form">
            <input {...tokenC} />
            <input {...tokenD} />
          </div>

          <button onClick={showStak.toggle} className="cta-button mint-nft-button">
            {!showStak.show ? "token staking show" : "token staking hide"}
          </button>
          {showStak.show && (
          <input {...tokenStakAdd} />
          )}

          <button onClick={addLiquidy} className="cta-button mint-nft-button">confirm</button>
        </form> 
      )}
      {!showForm.show && (

        <form>
          <h2>LP token remover</h2>
          <input {...amountWithdrawal} />
          <button onClick={removeLiquidy} className="cta-button mint-nft-button">confirm</button>


        </form>
      )}
    </>
  );
};
export default CreateDrop;
